w1 = Wine.create!(title: "Chateau Lafite Rothschild", year: 2009, varietal: "Bordeaux", second_img_url:"https://www.wine.com/product/images/w_768,h_1641,c_fit,q_auto:good,fl_progressive/111778fbs.jpg", description: "This is stunning for its ability to take massively endowed fig, currant paste and crushed plum fruit flavors and harness them with ultrasuave freshly roasted espresso, black tea and ganache notes. A seductive style, long and velvety, with the dense core of black fruit and smoldering iron just waiting and waiting. Best from 2020 through 2040.", score: 98, price: 1250, country: "France", region: "Pauillac, Bordeaux", img_url:"https://www.wine.com/product/images/w_480,c_fit,q_auto:good,fl_progressive/111778.jpg")

Wine.create!(title: "Nobilo", year: 2018, varietal: "Sauvignon Blanc", second_img_url:"https://www.wine.com/product/images/w_480,h_600,c_fit,q_auto:good,fl_progressive/vum5oz6efvpl587donkg.jpg", description: "Fresh, crisp and clean with zesty flavors of ripe tropical fruits, especially passionfruit and pineapple with subtle hints of green herbs. The wine is intensely flavored with balanced mouthwatering acidity and a generous finish.", score: 89, price: 12, country: "New Zealand ", region: "Marlborough", img_url:"https://www.wine.com/product/images/w_768,c_fit,q_auto:good,fl_progressive/dfhr3cjnfn6u7elqk75j.jpg")

Wine.create!(title: "Olivier Leflaive", year: 2017, varietal: "Chardonnay", second_img_url:"https://www.wine.com/product/images/w_480,h_600,c_fit,q_auto:good,fl_progressive/kxgvv1n8nfabwyz5ohpj.jpg", description: "Fermented and aged in wood, this is a rich wine. Its spice, ripe pineapple and mango flavors and dense texture show intense freshness as well as layers of toast, a complex texture and a short aging potential. Drink from 2021.", score: 82, price: 90, country: "France", region: "Puligny-Montrachet, Cote de Beaune, Cote d'Or, Burgundy, France", img_url:"https://www.wine.com/product/images/w_768,c_fit,q_auto:good,fl_progressive/wo6adueiwjczv2wu3qf3.jpg")

